package com.cisoft.shop;import android.widget.ImageView;import com.cisoft.myapplication.R;import org.kymjs.aframe.ui.activity.BaseSplash;public class WelcomeActivity extends BaseSplash {    @Override    protected void setRootBackground(ImageView view) {        view.setBackgroundResource(R.drawable.ic_launcher);    }    @Override    protected void redirectTo() {        super.redirectTo();        skipActivity(this, MainActivity.class);    }}